#!/bin/bash\n\n# LocalPulse Dashboard Startup Script\necho \"🚀 Starting LocalPulse Dashboard...\"\n\n# Check if Node.js is installed\nif ! command -v node &> /dev/null; then\n    echo \"❌ Node.js is not installed. Please install Node.js first.\"\n    exit 1\nfi\n\n# Start the API server in the background\necho \"📊 Starting API server...\"\ncd backend\nnpm start &\nAPI_PID=$!\ncd ..\n\n# Wait for the API server to start\necho \"⏳ Waiting for API server to start...\"\nsleep 3\n\n# Check if API server is running\nif curl -s http://localhost:3002/api/config > /dev/null; then\n    echo \"✅ API server is running on http://localhost:3002\"\nelse\n    echo \"⚠️ API server may not be running properly\"\nfi\n\n# Start a simple HTTP server for the dashboard\necho \"🌐 Starting dashboard web server...\"\nif command -v python3 &> /dev/null; then\n    echo \"📱 Dashboard available at: http://localhost:8080\"\n    echo \"🔄 The dropdowns will now work with real data!\"\n    echo \"📊 Try changing the timeframe and view filters\"\n    echo \"\"\n    echo \"Press Ctrl+C to stop both servers\"\n    python3 -m http.server 8080\nelse\n    echo \"📱 Open index.html in your browser\"\n    echo \"🔄 The dropdowns will now work with real data!\"\n    echo \"📊 Try changing the timeframe and view filters\"\n    open index.html\nfi\n\n# Cleanup when script exits\ntrap \"echo '🛑 Stopping servers...'; kill $API_PID 2>/dev/null; exit 0\" INT TERM 